-- ----------------------------
-- Table structure for article
-- ----------------------------
DROP TABLE IF EXISTS `article`;
CREATE TABLE `article` (
  `wid` int(11) NOT NULL AUTO_INCREMENT COMMENT '文章wid',
  `uid` int(11) DEFAULT NULL COMMENT '用户id',
  `title` varchar(30) DEFAULT NULL COMMENT '文章标题',
  `tag` char(8) DEFAULT NULL COMMENT '文章标签',
  `introduction` char(120) DEFAULT NULL COMMENT '文章简介',
  `conten` varchar(2400) DEFAULT NULL COMMENT '文章内容',
  `cover` varchar(120) DEFAULT NULL COMMENT '文章封面',
  `is_delete` int(11) DEFAULT '0' COMMENT '是否删除，0代表未删除，1代表已删除',
  `Hotlist` int(11) DEFAULT NULL COMMENT '是否显示热榜 0代表否，1代表是',
  `mun` int(11) DEFAULT NULL COMMENT '阅读量',
  `commentID` int(11) DEFAULT NULL COMMENT '文章评论ID',
  `created_user` varchar(50) DEFAULT NULL COMMENT '创建用户',
  `created_time` datetime DEFAULT NULL COMMENT '创建时间',
  `modified_user` varchar(50) DEFAULT NULL COMMENT '最后修改用户',
  `modified_time` datetime DEFAULT NULL COMMENT '最后修改时间',
  PRIMARY KEY (`wid`)
) ENGINE=InnoDB AUTO_INCREMENT=59 DEFAULT CHARSET=utf8;

